import { useNavigate, } from "react-router-dom";
import { useState } from 'react';

const Dashboard = () => {

  const navigate = useNavigate();

  const pageWrapper = {

    display: "flex",
    flexDirection: "column",
    minHeight: "90vh",
    backgroundColor: "#e3e3ed",

  }

  const titleText = {

    color: "black",
    fontSize: "2em",
    padding: "2%",
    textDecoration: "underline"

  }

  const gamesDisplayStyle = {

    display: "flex",
    flexDirection: "row",
    padding: "40px",

  }

  const gamesDisplayOuterStyle = {
    margin: "20px",
    padding: "20px",
    backgroundColor: "white",
    boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
    borderRadius: "30px"
  }

  const BlitzBoutButton = () => {

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
      setIsHover(true);
    };
    const handleMouseLeave = () => {
      setIsHover(false);
    };

    const buttonStyle = {
      border: "none",
      backgroundColor: isHover ? "rgba(0, 0, 0, 0.1)" : "rgba(0, 0, 0, 0)",
    }

    const pictureStyle = {
      height: "50px",
      width: "50px",
      borderRadius: "50%"
    };

    const handleLink = (e) => {

      // prevent the form from refreshing the whole page
      e.preventDefault();

      navigate("/games/BlitzBout");

    }

    return (
      <button onClick={(e) => handleLink(e)} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} style={buttonStyle}>
        <img src="./images/games/BlitzBout/titleImageCrown.png" alt="BlitzBout Title Crown" style={pictureStyle} />
      </button>
    );

  }

  return (
    <div id="dashboard" style={pageWrapper}>
      <h1 style={titleText}>Dashboard</h1>
      <div id="gamesDisplayOuter" style = {gamesDisplayOuterStyle}>
        <h2>Games</h2>
        <div id = "gamesDisplay" style = {gamesDisplayStyle}>
          <BlitzBoutButton />
        </div>
      </div>
    </div>
  );

};

export default Dashboard;
