import React from 'react'
import PropTypes from 'prop-types';

const BlitzBoutQuestionCreator = ({ gameName, gameKey, questionData }) => {

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "row",
        justifyItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "#334173"

    }

    // #e3e3ed

    const blueEmphasisStyle = {
        color: "#334173",
        fontWeight: "bold"
    }

    const AnswerCard = (answer) => {

        console.log(answer)

        const userCardStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const textStyle = {
            display: "inline-block",
            fontSize: "0.6em"
        }

        return (
            <div style={userCardStyle}>
                <h1 style={textStyle}>{answer.answer.answerText}</h1>
            </div>
        )
    }
    AnswerCard.propTypes = {
        answer: PropTypes.object
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <div id="joinGamePanel"></div>
                <h1>Join at http://localhost:3000/games/BlitzBout/join with the key<span style={blueEmphasisStyle}>{gameKey}</span></h1>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                <h1>{questionData.question}</h1>
                { questionData.answers ?
                    questionData.answers.map(
                        (answer, index) =>
                            <AnswerCard key={index} answer={answer} />
                    )
                    :
                    <h1>Err</h1>
                }
            </div>

        </div>

    );

};
BlitzBoutQuestionCreator.propTypes = {
    gameName: PropTypes.string,
    gameKey: PropTypes.string,
    questionData: PropTypes.object
}

export default BlitzBoutQuestionCreator;
