import React from 'react';
import { useState } from 'react';
import PropTypes from 'prop-types';
import { Form, Button } from "react-bootstrap";
import axios from "axios";

export default function LogIn({ setToken, setAvatar, setUsername, setEmail }) {

  const [userEmail, setUserEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState(false);

  var errorResponseMessage;

  const handleSubmit = (e) => {
    // prevent the form from refreshing the whole page
    e.preventDefault();
    // make a popup alert showing the "submitted" text

    // set configurations
    const configuration = {
      method: "post",
      url: "http://localhost:8080/log-in",
      data: {
        email: userEmail,
        password,
      },
    };

    // make the API call
    axios(configuration)
      .then((result) => {
        setLogin(true);
        // set the cookie token
        setToken(result.data.token);
        // set avatar and username
        setAvatar(result.data.avatar);
        setUsername(result.data.username);
        setEmail(result.data.email);
      })
      .catch((error) => {
        error = new Error();
        if (error.message) {
          errorResponseMessage = error.message;
        }
      });

  }

  const logInPageStyle = {

    display: "flex",
    paddingTop: "5%",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#e3e3ed",

  }

  const logInFormStyle = {

    display: "flex",
    padding: "5%",
    margin: "3%",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "white",

  }
  const logInFormElementStyle = {

    padding: "1%",
    margin: "1%",
    fontSize: "2.5em",

  }

  return (
    <div id="logInPage" style={logInPageStyle}>
      
      <Form onSubmit={(e) => handleSubmit(e)} style = {logInFormStyle}>
        <h2 style={logInFormElementStyle}>Log In</h2>
        {/* email */}
        <Form.Group controlId="formBasicEmail">
          <Form.Label style={logInFormElementStyle}>Email address</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={userEmail}
            onChange={(e) => setUserEmail(e.target.value)}
            placeholder="Enter email"
            style={logInFormElementStyle}
          />
        </Form.Group>

        {/* password */}
        <Form.Group controlId="formBasicPassword">
          <Form.Label style={logInFormElementStyle} >Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            style={logInFormElementStyle}
          />
        </Form.Group>

        {/* submit button */}
        <Button
          variant="primary"
          type="submit"
          onClick={(e) => handleSubmit(e)}
          style={logInFormElementStyle}
        >
          Login
        </Button>

        {/* display success message */}
        {login ? (
          <p className="text-success">You Are Logged in Successfully</p>
        ) : (
          <p className="text-danger">{errorResponseMessage ? errorResponseMessage : "You Are Not Logged in"}</p>
        )}

      </Form>

    </div>
  )
}

LogIn.propTypes = {
  setToken: PropTypes.func.isRequired,
  setAvatar: PropTypes.func.isRequired,
  setUsername: PropTypes.func.isRequired,
  setEmail: PropTypes.func.isRequired,
}
