import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authorizedByCookie from '../use_functions/authorizedByCookie';

async function loginUser(credentials) {

  return fetch('http://localhost:8080/api/account/log_in', {
    credentials: 'include',
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
    .then(data => data.json())
}

const LogIn = () => {

    const [username, setUserName] = useState();
    const [password, setPassword] = useState();

    const navigate = useNavigate();
    const  logInToDashboard = () =>{
      localStorage.removeItem('logged');
      navigate('/dashboard')
    }

    const handleSubmit = async e => {
      e.preventDefault();

      const result = await loginUser({
        username,
        password
      });

      if (result.success) {
        if ( authorizedByCookie() === true ) {
          // handle correct password
          logInToDashboard();
        }
        else {
          // handle incorrect password
          console.log("no cookie")
        }

        console.log("successful sign in attempt")
      }
      
    }  

    const loginWrapper = {

        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minHeight: "90vh"

    }

    return (

    <div style = {loginWrapper}>
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit} id = "logIn">
        <label id = "usernameButton">
          <p>Username</p>
          <input type="text" onChange={e => setUserName(e.target.value)}/>
        </label>
        <label id = "passwordButton">
          <p>Password</p>
          <input type="password" onChange={e => setPassword(e.target.value)}/>
        </label>
        <div id = "submitButton">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>

    );
};
  
export default LogIn;
