import React from 'react';
import { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useSearchParams, useNavigate } from "react-router-dom";

export default function CreateBlitzBoutGame({ token, avatar, username, email, favColor, gameSettings, setGameSettings }) {

    const [create, setCreate] = useState(false);
    const [chosenGame, setChosenGame] = useState("");
    const [errMsg, setErrMsg] = useState("");
    const { gotGameSettings, setGotGameSettings } = useState(false);
    const [setSearchParams] = useSearchParams();
    const navigate = useNavigate();

    /*
        Prelim grab needed stuff
    */

    async function getGameSettings() {
        // get game settings
        // set configurations
        const configuration = {
            method: "post",
            url: "http://localhost:8080/games/BlitzBout/get-gameSettings",
            data: {
                email: email,
            },
            headers: { Authorization: `Bearer ${token}` }
        };

        // make the API call
        axios(configuration)
            .then((result) => {

                setGameSettings(result.data.gameSettings);
                setGotGameSettings(true);

            })
            .catch((error) => {
                return (<h1>Err: {error.response.data.message}</h1>);
            });
    }

    useEffect(async () => {
        const getGameSettings2 = async () => {
            // get game settings
            // set configurations
            const configuration = {
                method: "post",
                url: "http://localhost:8080/games/BlitzBout/get-gameSettings",
                data: {
                    email: email,
                },
                headers: { Authorization: `Bearer ${token}` }
            };

            // make the API call
            axios(configuration)
                .then((result) => {

                    setGameSettings(result.data.gameSettings);
                    setGotGameSettings(true);

                })
                .catch((error) => {
                    return (<h1>Err: {error.response.data.message}</h1>);
                });
        }
        getGameSettings2();
    }, []);

    window.addEventListener("beforeunload", (event) => {
        if (!gotGameSettings) {
            getGameSettings();
            console.log("API call to get game settings before page reload");
        }
    });

    window.addEventListener("unload", (event) => {
        if (!gotGameSettings) {
            getGameSettings();
            console.log("API call to get game settings after page reload");
        }
    });


    /*
            handle create submit
    */

    const handleSubmit = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();
        // make a popup alert showing the "submitted" text

        // set configurations
        const configuration = {
            method: "post",
            url: "http://localhost:8080/games/BlitzBout/create-game",
            data: {
                token: token,
                username: username,
                avatar: avatar,
                email: email,
                favColor: favColor,
                chosenGame: chosenGame
            },
        };

        // make the API call
        axios(configuration)
            .then((result) => {

                navigate("/games/BlitzBout");
                setSearchParams(result.data.roomNumber);
                setCreate(true);

            })
            .catch((error) => {
                if (error.response.data.message) {
                    setErrMsg(error.response.data.message);
                }
                else {
                    setErrMsg("");
                }
            });

    }

    const handleSelectChange = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();

        setChosenGame(e);
    };

    /*
        styles
    */

    const logInPageStyle = {

        display: "flex",
        paddingTop: "3%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",

    }

    const logInFormStyle = {

        display: "flex",
        padding: "5%",
        margin: "3%",
        marginBottom: "0",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "white",
        boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
        borderRadius: "30px"

    }
    const logInFormElementStyle = {

        padding: "1%",
        margin: "1%",

    }

    const logInFormButtonStyle = {

        margin: "6%",

    }

    /*
        Components
    */

    return (
        <div id="logInPage" style={logInPageStyle}>

            <Form onSubmit={(e) => handleSubmit(e)} style={logInFormStyle}>

                <h2 style={logInFormElementStyle}>Create Game</h2>

                <Form.Group controlId="formBasicSubmit">

                    {/* submit button */}
                    <Button
                        variant="primary"
                        type="submit"
                        onClick={(e) => handleSubmit(e)}
                        style={logInFormButtonStyle}
                    >
                        Login
                    </Button>

                    {/* display success message */}
                    {create ? (
                        <p className="text-success">Created Game</p>
                    ) : (
                        <p className="text-danger">{errMsg ? errMsg : "Awaiting game creation"}</p>
                    )}

                </Form.Group>

                <Form.Group controlId="formBasicSubmit">

                    <h2 style={logInFormElementStyle}>Select Game</h2>
                    {/* select dropdown */}
                    <select
                        value={chosenGame}
                        onChange={(e) => handleSelectChange(e.target.value)}
                    >
                        {gameSettings.map(
                            (gameSetting, index) =>
                                <option value={gameSetting.name} key={index}>{gameSetting.name}</option>
                        )}
                    </select>


                </Form.Group>

            </Form>

        </div>
    )
}

CreateBlitzBoutGame.propTypes = {
    token: PropTypes.string.isRequired,
    avatar: PropTypes.string.isRequired,
    username: PropTypes.string.isRequired,
    email: PropTypes.string.isRequired,
    favColor: PropTypes.string.isRequired,
    setGameSettings: PropTypes.func.isRequired,
    gameSettings: PropTypes.array.isRequired,
}
