import React from 'react';
import { useState } from 'react';
import PropTypes from 'prop-types';
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function joinBlitzBoutGame({ token, avatar, username, email }) {

  const [gameKey, setGameKey] = useState("");
  const [login, setLogin] = useState(false);
  const [errMsg, setErrMsg] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    // prevent the form from refreshing the whole page
    e.preventDefault();
    // make a popup alert showing the "submitted" text

    // set configurations
    const configuration = {
      method: "post",
      url: "http://localhost:8080/games/BlitzBout/create-gameSetting",
      data: {
        email: email,
        token: token,
        avatar: avatar,
        username: username, 
        gameKey: gameKey
      },
    };

    // make the API call
    axios(configuration)
      .then((result) => {
        
        navigate("/account/dashboard");

      })
      .catch((error) => {
        if (error.response) {
          setErrMsg(error.response.data.message);
          setLogin(true)
        }
        else {
          setErrMsg("");
        }
      });

  }

  const logInPageStyle = {

    display: "flex",
    paddingTop: "3%",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#e3e3ed",

  }

  const logInFormStyle = {

    display: "flex",
    padding: "5%",
    margin: "3%",
    marginBottom: "0",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "white",
    boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
    borderRadius: "30px"

  }
  const logInFormElementStyle = {

    padding: "1%",
    margin: "1%",

  }
  const logInFormLabelStyle = {

    padding: "1%",
    marginTop: "6%",

  }

  const logInFormButtonStyle = {

    margin: "6%",

  }

  return (
    <div id="logInPage" style={logInPageStyle}>

      <Form onSubmit={(e) => handleSubmit(e)} style={logInFormStyle}>
        <h2 style={logInFormElementStyle}>Log In</h2>
        {/* email */}
        <Form.Group controlId="formBasicEmail">
          <Form.Label style={logInFormLabelStyle}>Game Key</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={gameKey}
            onChange={(e) => setGameKey(e.target.value)}
            placeholder="Enter email"
            style={logInFormElementStyle}
          />
        </Form.Group>

        {/* submit button */}
        <Button
          variant="primary"
          type="submit"
          onClick={(e) => handleSubmit(e)}
          style={logInFormButtonStyle}
        >
          Login
        </Button>

        {/* display success message */}
        {login ? (
          <p className="text-success">You Are Logged in Successfully</p>
        ) : (
          <p className="text-danger">{errMsg ? errMsg : "You are not logged in"}</p>
        )}

      </Form>

    </div>
  )
}

LogIn.propTypes = {
  setToken: PropTypes.func.isRequired,
  setAvatar: PropTypes.func.isRequired,
  setUsername: PropTypes.func.isRequired,
  setEmail: PropTypes.func.isRequired,
  setFavColor: PropTypes.func.isRequired,
  token: PropTypes.string
}
