import { Navigate, } from "react-router-dom";

const Dashboard = () => {

  const pageWrapper = {

    display: "flex",
    flexDirection: "column",
    minHeight: "90vh",
    backgroundColor: "#e3e3ed",

  }

  const titleText = {

    color: "black",
    fontSize: "2em",
    padding: "2%",
    textDecoration: "underline"

  }

  const BlitzBoutButton = () => {

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
      setIsHover(true);
    };
    const handleMouseLeave = () => {
      setIsHover(false);
    };

    const buttonStyle = {
      border: "none",
      backgroundColor: isHover ? "rgba(0, 0, 0, 0.1)" : "rgba(0, 0, 0, 0)",
    }

    const pictureStyle = {
      height: "50px",
      width: "50px",
      borderRadius: "50%"
    };

    const handleLink = (e) => {

      // prevent the form from refreshing the whole page
      e.preventDefault();

      return (
        <Navigate to="/games/BlitzBout" />
      );

    }

    return (
      <button onClick={(e) => handleLink(e)} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} style={buttonStyle}>
          <img src="./images/games/BlitzBout/titleImageCrown" alt="BlitzBout Title Image Crown" style={pictureStyle} />
      </button>
    );

  }

  return (
    <div id="dashboard" style={pageWrapper}>
      <h1 style={titleText}>Dashboard</h1>
      <div id="games">

      </div>
    </div>
  );

};

export default Dashboard;
