import React from 'react'
import PropTypes from 'prop-types';
import AvatarPicture from "../components/AvatarPicture"

const BlitzBoutLobbyCreator = ({ users, gameName, gameKey, deleteUser, startGame }) => {

    const handleDeleteUser = (e, emailToDelete) => {
        e.preventDefault();

        deleteUser(emailToDelete);
    }

    const handleStartGame = (e) => {
        e.preventDefault();

        startGame();
    }

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "row",
        justifyItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "#334173"

    }

    // #e3e3ed

    const mainContentWrapperStyle = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    }

    // #e3e3ed

    const blueEmphasisStyle = {
        //color: "#334173",
        fontWeight: "bold"
    }

    const nextButtonStyle = {
        float: "right"
    }

    const UserCard = (user) => {

        const userCardStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const textStyle = {
            display: "inline-block",
            fontSize: "0.6em"
        }

        const imageStyle = {
            float: "left",
            margin: "5%"
        }

        return (
            <div style={userCardStyle}>
                <div style={imageStyle}>
                    <AvatarPicture avatar={user.user.avatar} dimension="40px" />
                </div>
                <h1 style={textStyle}>{user.user.username}</h1>
                <button onClick={(e) => handleDeleteUser(e, user.user.email)}>Click me</button>
            </div>
        )
    }
    UserCard.propTypes = {
        user: PropTypes.object
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <div id="joinGamePanel"></div>
                <h1>Join at http://localhost:3000/games/BlitzBout/join with the key<span style={blueEmphasisStyle}>{gameKey}</span></h1>
            </main>

            <div id="mainContentWrapper" style={mainContentWrapperStyle}>
                <div id="topper" >
                    <h1>{gameName}</h1>
                    <button onClick={(e) => handleStartGame(e)} style={nextButtonStyle}>Click me</button>
                </div>
                <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                    {
                        users.map(
                            (user, index) =>
                                <UserCard key={index} user={user} />
                        )
                    }
                </div>
            </div>

        </div>

    );

};
BlitzBoutLobbyCreator.propTypes = {
    users: PropTypes.array,
    gameName: PropTypes.string,
    gameKey: PropTypes.string,
    deleteUser: PropTypes.func,
    startGame: PropTypes.func,
}

export default BlitzBoutLobbyCreator;
