import React from 'react'
import PropTypes from 'prop-types';
import AvatarPicture from "../components/AvatarPicture"

const BlitzBoutLobby = ({ users, gameName, gameKey }) => {

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "row",
        justifyItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "#334173"

    }

    // #e3e3ed

    const blueEmphasisStyle = {
        color: "#334173",
        fontWeight: "bold"
    }

    const UserCard = (user) => {

        const userCardStyle = {
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding:"1%",
            margin: "2%"
        }

        const textStyle = {
            display: "inline-block"
        }

        const imageStyle = {
            float: "left",
            padding: "1%"
        }

        return (
            <div style={userCardStyle}>
                <div style={imageStyle}>
                    <AvatarPicture avatar={user.user.avatar} dimension="50px" />
                </div>
                <h1 style = {textStyle}>{user.user.username}</h1>
            </div>
        )
    }
    UserCard.propTypes = {
        user: PropTypes.object
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <div id="joinGamePanel"></div>
                <h1>Join at http://localhost:3000/games/BlitzBout/join with the key<span style={blueEmphasisStyle}>{gameKey}</span></h1>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                {
                    users.map(
                        (user, index) =>
                            <UserCard key={index} user={user} />
                    )
                }
            </div>

        </div>

    );

};
BlitzBoutLobby.propTypes = {
    users: PropTypes.array,
    gameName: PropTypes.string,
    gameKey: PropTypes.string
}

export default BlitzBoutLobby;
