import AvatarPicture from "./AvatarPicture";
import PropTypes from 'prop-types';

const AccountDropdown = ( { avatar, username, email } ) => {

    const emailTextStyle = {
        color: "#514C48",
        fontSize: "1.5em"
    }

    const helloTextStyle = {
        color: "black",
        fontWeight: "bold",
        fontSize: "2em"
    }

    const dropdownBoxStyle = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#f0f0f0",
        minWidth: "450px",
        padding: "12px 16px",
        overflow: "hidden"
    }

    const userInfoBoxStyle = {
        padding: "3px 4px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    }

    const userLinksBox = {
        margin: "3px 4px",
        backgroundColor: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minWidth: "300px"
    }

    return (
        
        <div id = "dropdownBox" style = {dropdownBoxStyle}>
            <div id = "userInfoBox" style = {userInfoBoxStyle}>
                <h1 style = {emailTextStyle}>{email}</h1>
                <AvatarPicture avatar = {avatar} dimension = "70px"/>
                <h1 style = {helloTextStyle}>Hi, {username}!</h1>
            </div>
            <div id = "userLinksBox" style = {userLinksBox}>
                <h3>Settings</h3>
                <h3>Dashboard</h3>
                <h3>Games</h3>
            </div>
        </div>
    );

};
  
export default AccountDropdown;  

AccountDropdown.propTypes = {
    avatar: PropTypes.string, 
    username: PropTypes.string,
    email: PropTypes.string
}
