import React from 'react'
import PropTypes from 'prop-types';
import { useState } from 'react';

const BlitzBoutQuestion = ({ gameName, gameKey, questionData, questionState, newAnswer }) => {

    const [userAnswer, setUserAnswer] = useState(0);

    const handleNewAnswer = (e, answer) => {
        e.preventDefault();
        setUserAnswer(answer)
        newAnswer(answer);
    }

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "row",
        justifyItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "#334173"

    }

    // #e3e3ed

    const AnswerCard = ({ answer, index }) => {

        const answeredStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const userAnswerStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "rgba(0,0,0,0.05)",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const answerButtonStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const textStyle = {
            display: "inline-block",
            fontSize: "0.6em"
        }

        return (

            questionState === "answered" ?
                <div style={userAnswer === index ? userAnswerStyle : answeredStyle}>
                    <h1 style={textStyle}>{answer.answerText}</h1>
                </div>
                :
                <button style={answerButtonStyle} onClick={(e) => handleNewAnswer(e, index)}>{answer.answerText}</button>

        )
    }
    AnswerCard.propTypes = {
        answer: PropTypes.object,
        index: PropTypes.number
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                {questionData ? <h1>{questionData.question}</h1> : <h1>Err</h1>}
                {questionData ?
                    (questionData.answers ?
                        questionData.answers.map(
                            (answer, index) =>
                                <AnswerCard key={index} answer={answer} index={index} />
                        )
                        :
                        <h1>Err</h1>
                    )
                    :
                    <h1>Err</h1>
                }

            </div>

        </div>

    );

};
BlitzBoutQuestion.propTypes = {
    gameName: PropTypes.string,
    gameKey: PropTypes.string,
    questionData: PropTypes.object,
    newLeaderboard: PropTypes.func,
    questionState: PropTypes.string,
    newAnswer: PropTypes.func
}

export default BlitzBoutQuestion;
