import NoPage from "./NoPage";
import AvatarPicture from "../components/AvatarPicture";
import PropTypes from 'prop-types';

const Settings = ( { avatar, username, email, token } ) => {

    if(!token) {
        return (
            <NoPage />
        );
    }

    const welcomeWrapperStyle = {
  
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      backgroundColor: "#e3e3ed",
      fontSize: "2.5em",
      paddingTop: "5%",
      fontFamily: "poppins"
  
    }

    const settingsDivStyle = {
        
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins"
    
    }

    const selectAvatarStyle = {

        display: "flex",
        flexDirection: "row",

    }

    const avatarDisplayStyle = {

        display: "flex",
        flexDirection: "column",
        alignItems: "center",

    }
  
    const blueEmphasisStyle = {
      color: "#334173",
      fontWeight: "bold"
    }
  
    const paragraphStyle = {
      color: "#514C48",
      fontSize: "0.8em"
    }

    const commentStyle = {
        color: "#514C48",
        fontSize: "1.2em"
    }
  
      return (
  
        <div id = "settingsDiv" style = {settingsDivStyle}>
  
          <main id = "welcomeWrapper" style = {welcomeWrapperStyle}>
            <AvatarPicture avatar = {avatar} dimension = "100px"/>
            <h1><span style = {blueEmphasisStyle}>Welcome</span>, {username}</h1>
            <p style = {paragraphStyle}>Manage your info, preferences, and security to make Imersi the best possible experience for you.</p>
            <p style = {paragraphStyle}>{email}</p>
          </main>

          <div id = "selectAvatar" style = {selectAvatarStyle}>
            <div id = "avatarDisplay" style = {avatarDisplayStyle}>
                <h3>Change your Avatar</h3>
                <AvatarPicture avatar = {avatar} dimension = "100px"/>
                <p style = {paragraphStyle}>Your current avatar</p>
            </div>
            
          </div>
  
        </div>
          
      );
      
    };
    
  export default Settings;  

Settings.propTypes = {
    token: PropTypes.string,
    avatar: PropTypes.string, 
    username: PropTypes.string,
    email: PropTypes.string
}
