import AvatarPicture from "../components/AvatarPicture";
import PropTypes from 'prop-types';
import { useState } from 'react';
import { Form, Button } from "react-bootstrap";
import axios from "axios";

const Settings = ( { avatar, username, email, token, setAvatar } ) => {

    const [userAvatar, setUserAvatar] = useState({avatar});
    const avatarList = ["baseUser", "blackDog", "brownBear", "cat", "chicken", "giraffe", "gorilla", "lion", "meerkat", "orangeBear", "orangeDog", "panda", "rabbit", "seaLion", "shark", "wolf"];

    const handleSubmit = (e) => {

        setUserAvatar(avatarList[5]);

        // prevent the form from refreshing the whole page
        e.preventDefault();
        // make a popup alert showing the "submitted" text
    
        // set configurations
        const configuration = {
          method: "post",
          url: "http://localhost:8080/update-avatar",
          data: {
            email: email,
            avatar: userAvatar,
            token
          },
        };
    
        // make the API call
        axios(configuration)
          .then((result) => {
            // set avatar and username
            setAvatar(result.data.avatar);
          })
          .catch((error) => {
            error = new Error();
        });
    
    }

    const welcomeWrapperStyle = {
  
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      backgroundColor: "#e3e3ed",
      fontSize: "2.5em",
      paddingTop: "5%",
      fontFamily: "poppins"
  
    }

    const settingsDivStyle = {
        
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins"
    
    }

    const selectAvatarStyle = {

        display: "flex",
        flexDirection: "row",
        padding: "40px"

    }

    const avatarDisplayStyle = {

        display: "flex",
        flexDirection: "column",
        alignItems: "center",

    }
  
    const blueEmphasisStyle = {
      color: "#334173",
      fontWeight: "bold"
    }
  
    const paragraphStyle = {
      color: "#514C48",
      fontSize: "0.8em"
    }

    const commentStyle = {
        color: "#514C48",
        fontSize: "1.2em"
    }
  
      return (
  
        <div id = "settingsDiv" style = {settingsDivStyle}>
  
          <main id = "welcomeWrapper" style = {welcomeWrapperStyle}>
            <AvatarPicture avatar = {avatar} dimension = "100px"/>
            <h1><span style = {blueEmphasisStyle}>Welcome</span>, {username}</h1>
            <p style = {paragraphStyle}>Manage your info, preferences, and security to make Imersi the best possible experience for you.</p>
            <p style = {paragraphStyle}>{email}</p>
          </main>

          <div id = "selectAvatar" style = {selectAvatarStyle}>
            <div id = "avatarDisplay" style = {avatarDisplayStyle}>
                <h3>Change your Avatar</h3>
                <AvatarPicture avatar = {avatar} dimension = "100px"/>
                <p style = {commentStyle}>Your current avatar</p>
                <Form onSubmit={(e)=>handleSubmit(e)}>
                    <Button variant="primary" type="submit" onClick={(e) => handleSubmit(e)}>
                        Change
                    </Button>
                </Form>
            </div>
            
          </div>
  
        </div>
          
      );
      
    };
    
  export default Settings;  

Settings.propTypes = {
    token: PropTypes.string,
    avatar: PropTypes.string, 
    username: PropTypes.string,
    email: PropTypes.string,
    setAvatar: PropTypes.func.isRequired
}
