import React from 'react'
import PropTypes from 'prop-types';

const BlitzBoutAnswers = ({ gameName, gameKey, answerData, answerScore, score, userAnswer }) => {

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutAnswersStyle = {
        display: "flex",
        justifyContent: "space-around",
        minWidth: "80%"
    }

    // #e3e3ed

    const blitzBoutAnswersWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins",
        fontColor: "#334173",
        minWidth: "80%"

    }

    const AnswerCard = ({ answer, index }) => {

        const userCardStyleRight = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "green",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const userCardStyleWrong = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "red",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const userCardStyleUndone = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "rgba(0,0,0,0.06)",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const textStyle = {
            display: "inline-block",
            fontSize: "0.6em"
        }

        return (
            <div style={answer.state ? userCardStyleRight : (userAnswer === index ? userCardStyleWrong : userCardStyleUndone)}>

                <h1 style={textStyle}>{answer.answerText}</h1>
                {userAnswer === index ? <h1>+{answerScore}</h1> : <></>}

            </div>
        )
    }
    AnswerCard.propTypes = {
        answer: PropTypes.object,
        index: PropTypes.number
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutAnswersWrapper" style={blitzBoutAnswersWrapperStyle}>
                <div>
                    <h1>Score: {score}</h1>
                </div>
                <div>
                    {answerData ? <h1>{answerData.question}</h1> : <h1>Err</h1>}
                </div>
                <div id="blitzBoutAnswers" style={blitzBoutAnswersStyle}>
                    {answerData ?
                        (answerData.answers ?
                            answerData.answers.map(
                                (answer, index) =>
                                    <AnswerCard key={index} answer={answer} index={index} />
                            )
                            :
                            <h1>Err</h1>
                        )
                        :
                        <h1>Err</h1>
                    }
                </div>


            </div>

        </div>

    );

};
BlitzBoutAnswers.propTypes = {
    gameName: PropTypes.string,
    gameKey: PropTypes.string,
    answerData: PropTypes.object,
    answerScore: PropTypes.number,
    score: PropTypes.number,
    userAnswer: PropTypes.number
}

export default BlitzBoutAnswers;
