import { io } from 'socket.io-client'
import React from 'react'
import { useSearchParams } from 'react-router-dom';
import PropTypes from 'prop-types';

const BlitzBoutLobby = (email) => {

    const [searchParams] = useSearchParams();

    const blitzBoutMainWrapperStyle = {

        display: "flex",
        paddingTop: "5%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins"

    }

    const blueEmphasisStyle = {
        color: "#334173",
        fontWeight: "bold"
    }

     React.useEffect(()=>{

        const authObject = {
            email: email,
            gameKey: searchParams.get("gameKey")
        };
        const socket = io('http://localhost:8080')

        socket.on("connection", (data) => {
            socket.emit("joinGame", authObject)
        })

        socket.on("updatePlayers", (data) => {
            console.log("hi")
        })
     
     },[email, searchParams])    

    return (

        <div id="blitzBout">

            <main id="blitzBoutMainWrapper" style={blitzBoutMainWrapperStyle}>
                <h1><span style={blueEmphasisStyle}>BlitzBout</span>.</h1>
            </main>

        </div>

    );

};
BlitzBoutLobby.propTypes = {
    email: PropTypes.string.isRequired
}

export default BlitzBoutLobby;
