import AvatarPicture from "./AvatarPicture";
import AccountDropdown from "./AccountDropdown";
import PropTypes from 'prop-types';
import { useState } from 'react';

const AccountButton = ( { avatar, username, email } ) => {

    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
      setIsHover(true);
    };
    const handleMouseLeave = () => {
      setIsHover(false);
    };

    const dropdownStyle = {
        position: "relative",
        display: "inline-block",
    }

    const dropdownContentStyle = {
        display: isHover ? "block" : "none",
        position: "absolute",
        zIndex: 1,
        right: "20px",
        overflow: "hidden",
        backgroundColor: "rgba(0, 0, 0, 0)",
        padding: "40px",
        minWidth: "500px"
    }

    return (
        <div id = "dropdown" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} style = {dropdownStyle}>
            <AvatarPicture avatar = {avatar} dimension = "40px"/>
            <div id = "dropdownContent" style = {dropdownContentStyle}>
                <AccountDropdown email = {email} username = {username} avatar = {avatar}/>
            </div>
            
        </div>
    );

};
  
export default AccountButton;  

AccountButton.propTypes = {
    avatar: PropTypes.string, 
    username: PropTypes.string,
    email: PropTypes.string
}
