import React from 'react';
import { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useSearchParams, useNavigate } from "react-router-dom";

export default function CreateBlitzBoutGame({ token, avatar, username, email, favColor, gameSettings, setGameSettings }) {

    const [create, setCreate] = useState(false);
    const [chosenGame, setChosenGame] = useState("");
    const [errMsg, setErrMsg] = useState("");
    const [setSearchParams] = useSearchParams();
    const navigate = useNavigate();

    /*
        Prelim grab needed stuff
    */

    /*
    async function getGameSettings() {
        // get game settings
        // set configurations
        const configuration = {
            method: "post",
            url: "http://localhost:8080/games/BlitzBout/get-gameSettings",
            data: {
                email: email,
            },
            headers: { Authorization: `Bearer ${token}` }
        };

        // make the API call
        axios(configuration)
            .then((result) => {

                setGameSettings(result.data.gameSettings);

            })
            .catch(

            );
    }

    useEffect(() => getGameSettings(), []); 

    */

    useEffect(() => {

        console.log(gameSettings);

        function getGameSettings() {

            console.log("Attempting load of required resources...")

            // get game settings
            // set configurations
            const configuration = {
                method: "post",
                url: "http://localhost:8080/games/BlitzBout/get-gameSettings",
                data: {
                    email: email,
                },
                headers: { Authorization: `Bearer ${token}` }
            };

            // make the API call
            axios(configuration)
                .then((result) => {

                    setGameSettings(result.data.gameSettings);
                })
                .catch(
            );
        }
        if (!gameSettings[0]) {
            getGameSettings();
        }

    }, [email, token, gameSettings, setGameSettings]);

    /*
            handle create submit
    */

    const handleSubmit = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();
        // make a popup alert showing the "submitted" text

        // set configurations
        const configuration = {
            method: "post",
            url: "http://localhost:8080/games/BlitzBout/create-game",
            data: {
                token: token,
                username: username,
                avatar: avatar,
                email: email,
                favColor: favColor,
                chosenGame: chosenGame
            },
        };

        // make the API call
        axios(configuration)
            .then((result) => {

                navigate("/games/BlitzBout");
                setSearchParams(result.data.roomNumber);
                setCreate(true);

            })
            .catch((error) => {
                if (error.response.data.message) {
                    setErrMsg(error.response.data.message);
                }
                else {
                    setErrMsg("");
                }
            });

    }

    const handleSelectChange = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();

        setChosenGame(e);
    };

    const handleCreateNewBBGame = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();
        navigate("/games/BlitzBout/create-new-BlitzBout-game");
    }

    /*
        styles
    */

    const createBBPageStyle = {

        display: "flex",
        paddingTop: "3%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",

    }

    const createBBGameBoxStyle = {

        display: "flex",
        padding: "5%",
        margin: "4%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "white",
        boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
        borderRadius: "30px",
        minWidth: "40%"

    }

    const createBBGameFormStyle = {

        display: "flex",
        flexDirection: "row",
        minWidth: "80%",
        justifyContent: "center",

    }

    const createBBGameFormElementStyle = {

        padding: "1%",
        margin: "1%",

    }

    const createBBGameFormGroupStyle = {

        padding: "1%",
        margin: "1%",
        minWidth: "45%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",

    }

    const createBBGameFormButtonStyle = {

        margin: "6%",

    }

    const titleText = {
        padding: "1%",
        margin: "1%",
        fontSize: "3em"
    }

    /*
        Components
    */

    return (
        <div id="createBBPage" style={createBBPageStyle}>

            <div id="createBBGamePageBox" style={createBBGameBoxStyle}>

                <h2 style={titleText}>Create Game</h2>

                <Form onSubmit={(e) => handleSubmit(e)} style={createBBGameFormStyle}>

                    <Form.Group controlId="formBasicSelect" style={createBBGameFormGroupStyle}>

                        <h2 style={createBBGameFormElementStyle}>Select Game</h2>
                        {/* select dropdown */}
                        <Form.Select
                            value={chosenGame}
                            onChange={(e) => handleSelectChange(e.target.value)}
                        >
                            {gameSettings.map(
                                (gameSetting, index) =>
                                    <option value={gameSetting.name} key={index}>{gameSetting.name}</option>
                            )}
                        </Form.Select>
                        {/* submit button */}
                        <Button
                            variant="primary"
                            type="createNewBBGame"
                            onClick={(e) => handleCreateNewBBGame(e)}
                            style={createBBGameFormButtonStyle}
                        >
                            Create
                        </Button>

                    </Form.Group>

                    <Form.Group controlId="formBasicSubmit" style={createBBGameFormGroupStyle}>

                        <h2 style={createBBGameFormElementStyle}>Create Game</h2>

                        {/* submit button */}
                        <Button
                            variant="primary"
                            type="submit"
                            onClick={(e) => handleSubmit(e)}
                            style={createBBGameFormButtonStyle}
                        >
                            Create
                        </Button>

                        {/* display success message */}
                        {
                            chosenGame !== undefined ?
                                (create ? (
                                    <p className="text-success">Created Game</p>
                                ) : (
                                    <p className="text-danger">{errMsg ? errMsg : "Awaiting game creation"}</p>
                                ))
                                :
                                (
                                    <p className="text-danger">No game selected</p>

                                )
                        }

                    </Form.Group>

                </Form>
            </div>

        </div>
    )
}

CreateBlitzBoutGame.propTypes = {
    token: PropTypes.string.isRequired,
    avatar: PropTypes.string.isRequired,
    username: PropTypes.string.isRequired,
    email: PropTypes.string.isRequired,
    favColor: PropTypes.string.isRequired,
    setGameSettings: PropTypes.func.isRequired,
    gameSettings: PropTypes.array.isRequired,
}
