import React from 'react'
import PropTypes from 'prop-types';
import AvatarPicture from "../components/AvatarPicture"
import { useEffect, useState } from 'react';

const BlitzBoutLobbyCreator = ({ users, gameName, gameKey, deleteUser, startGame, errorMsg }) => {

    console.log(errorMsg)

    const [display, setDisplay] = useState(false)

    const msgStyle = {
        display: display ? "flex" : "none"
    }

    useEffect(() => {
        setDisplay(true)
        const timeId = setTimeout(() => {
            // After 3 seconds set the show value to false
            setDisplay(false)
        }, 3000)

        return () => {
            clearTimeout(timeId)
        }
    }, [errorMsg]);

    const handleDeleteUser = (e, emailToDelete) => {
        e.preventDefault();

        deleteUser(emailToDelete);
    }

    const handleStartGame = (e) => {
        e.preventDefault();

        startGame();
    }

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "1%",
        flexDirection: "row",
        justifyItems: "center",
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins",
        fontColor: "#334173",
        minWidth: "80%"

    }

    // #e3e3ed

    const mainContentWrapperStyle = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins",
        fontColor: "#334173"
    }

    // #e3e3ed

    const blueEmphasisStyle = {
        //color: "#334173",
        fontWeight: "bold"
    }

    const nextButtonStyle = {
        float: "right",
        border: "grey solid 2px",
        padding: "1%"
    }

    const topperStyle = {
        minWidth: "80%",
        margin: "3%",
        marginBottom: "0"
    }

    const titleStyle = {
        display: "inline-block",
    }

    const keyStyle = {
        float: "right"
    }

    const joinGamePanelStyle = {
        backgroundColor: "white",
        borderRadius: "10px",
        padding: "1%",
        margin: "2%",
        display: "flex",
        justifyContent: "space-around",
        minWidth: "80%"
    }

    const UserCard = (user) => {

        const userCardStyle = {
            display: "flex",
            justifyContent: "space-around",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "30%",
            maxWidth: "40%"
        }

        const textWrapperStyle = {
            overflow: "hidden",
            minWidth: "50%",
            margin: "1%",
            display: "flex",
            alignItems: "center"
        }

        const imageStyle = {
            float: "left",
            margin: "5%"
        }

        const buttonStyle = {
            minWidth: "15%",
            margin: "1%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
        }

        return (
            <div style={userCardStyle}>
                <div style={imageStyle}>
                    <AvatarPicture avatar={user.user.avatar} dimension="40px" />
                </div>
                <div style={textWrapperStyle}>
                    <h5>{user.user.username}</h5>
                </div>
                <button style={buttonStyle} onClick={(e) => handleDeleteUser(e, user.user.email)}>
                    X
                </button>
            </div>
        )
    }
    UserCard.propTypes = {
        user: PropTypes.object
    }

    return (

        <>


            <div id="blitzBoutLobby">

                <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                    <div id="joinGamePanel" style={joinGamePanelStyle}>
                        <div id="Join">
                            <p>Join at</p>
                            <h5><span style={blueEmphasisStyle}>http://localhost:3000/games/BlitzBout/join</span></h5>
                        </div>
                        <div style={keyStyle}>
                            <p>GameKey:</p>
                            <h1><span style={blueEmphasisStyle}>{gameKey}</span></h1>
                        </div>
                    </div>
                </main>

                <div id="mainContentWrapper" style={mainContentWrapperStyle}>
                    <div id="topper" style={topperStyle}>
                        <h1 style={titleStyle}>{gameName}</h1>
                        <button onClick={(e) => handleStartGame(e)} style={nextButtonStyle}>Start game!</button>
                    </div>
                    <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                        {
                            users.map(
                                (user, index) =>
                                    <UserCard key={index} user={user} />
                            )
                        }
                    </div>
                </div>

            </div>
            <h1 style = {msgStyle}>{errorMsg}</h1>
        </>
    );

};
BlitzBoutLobbyCreator.propTypes = {
    users: PropTypes.array,
    gameName: PropTypes.string,
    gameKey: PropTypes.string,
    deleteUser: PropTypes.func,
    startGame: PropTypes.func,
    errorMsg: PropTypes.string
}

export default BlitzBoutLobbyCreator;
