import { io } from 'socket.io-client'
import React from 'react'
import PropTypes from 'prop-types';
import { useState } from 'react';
import BlitzBoutLobbyCreator from './BlitzBoutLobbyCreator';
import { useNavigate } from "react-router-dom";

const socket = io.connect('http://localhost:8080')

const BlitzBoutCreator = ({ email, gameKey, setGameKey, token }) => {

    const [gameState, setGameState] = useState("lobby");
    const [users, setUsers] = useState([]);
    const [gameName, setGameName] = useState("default");
    const navigate = useNavigate();

    React.useEffect(() => {

        const authObject = {
            email: email,
            gameKey: gameKey,
            token: token
        };

        socket.emit("creatorJoin", authObject)

        socket.on('connection', (data) => {
            socket.emit("joinGame", authObject)
        })

        socket.on("updatePlayers", (data) => {
            setUsers(data)
            console.log("new user state")
        })

        socket.on("gameState", (data) => {
            setGameState(data)
            console.log("new gamestate")
        })

        socket.on("gameName", (data) => {
            setGameName(data)
            console.log("new gameName")
        })

        socket.on("disconnection", (data) => {
            setGameKey("");
            console.log("hi3")
        })

        socket.on("joinFail", (data) => {
            navigate("/games/BlitzBout/create")
            console.log(data)
        })

        socket.on("connect_error", (err) => {
            console.log(`connect_error due to ${err.message}`);
        });

    }, [email, gameKey, setGameKey])

    const deleteUser = (emailToDelete) => {

        const sendObject = {
            email: email,
            gameKey: gameKey,
            token: token,
            userToDelete: emailToDelete
        };

        socket.emit("deleteUser", sendObject)
    }

    switch (gameState) {
        case "":
            return <BlitzBoutLobbyCreator users={users} gameKey={gameKey} gameName={gameName} deleteUser={deleteUser}/>
        case "lobby":
            return <BlitzBoutLobbyCreator users={users} gameKey={gameKey} gameName={gameName} deleteUser={deleteUser}/>
        case "question":
            return <BlitzBoutLobbyCreator users={users} gameKey={gameKey} gameName={gameName} deleteUser={deleteUser}/>
        case "scoreboard":
            return <BlitzBoutLobbyCreator users={users} gameKey={gameKey} gameName={gameName} deleteUser={deleteUser}/>
        default:
            return <h1>hi</h1>
    }

};
BlitzBoutCreator.propTypes = {
    email: PropTypes.string,
    gameKey: PropTypes.string,
    setGameKey: PropTypes.func,
    token: PropTypes.string
}

export default BlitzBoutCreator;
