import React from 'react'
import PropTypes from 'prop-types';
import AvatarPicture from '../components/AvatarPicture';

const BlitzBoutLeaderboard = ({ gameName, username, score, leaderboardData }) => {

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontFamily: "poppins",
        fontColor: "#334173",
        minHeight: "50%"

    }

    // #e3e3ed

    const userDisplayStyle = {
        display: "flex",
        justifyContent: "space-around",
        minWidth: "80%",
        margin: "1%"
    }

    const scoreStyle = {
        backgroundColor: "#334173",
        minWidth: "10%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    }

    const UserCard = (user) => {

        const userCardStyle = {
            display: "flex",
            flexDirection: "row",
            justifyItems: "center",
            backgroundColor: "white",
            fontSize: "1em",
            fontFamily: "poppins",
            fontColor: "#334173",
            padding: "2%",
            margin: "2%",
            minWidth: "25%"
        }

        const textStyle = {
            display: "inline-block",
            fontSize: "0.6em"
        }

        const imageStyle = {
            float: "left",
            margin: "5%"
        }

        return (
            <div style={userCardStyle}>
                <div style={imageStyle}>
                    <AvatarPicture avatar={user.user.avatar} dimension="40px" />
                </div>
                <h1 style={textStyle}>{user.user.place}. {user.user.username}: {user.user.score}</h1>
            </div>
        )
    }
    UserCard.propTypes = {
        user: PropTypes.object
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                <div style={userDisplayStyle}>
                    <div><h1>{username}</h1></div>
                    <div style={scoreStyle}><h1>{score}</h1></div>
                </div>
                <h1>Leaderboard: </h1>
                {leaderboardData ?
                    leaderboardData.map(
                        (user, index) =>
                            <UserCard key={index} user={user} />
                    )
                    :
                    <h1>Err</h1>
                }

            </div>

        </div>

    );

};
BlitzBoutLeaderboard.propTypes = {
    gameName: PropTypes.string,
    username: PropTypes.string,
    leaderboardData: PropTypes.array,
    score: PropTypes.string,
}

export default BlitzBoutLeaderboard;
