import { io } from 'socket.io-client'
import React from 'react'
import PropTypes from 'prop-types';

const socket = io('http://localhost:8080')

const BlitzBoutPlay = ({email, gameKey, setGameKey}) => {

    const blitzBoutMainWrapperStyle = {

        display: "flex",
        paddingTop: "5%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins"

    }

    const blueEmphasisStyle = {
        color: "#334173",
        fontWeight: "bold"
    }

    console.log(gameKey)

     React.useEffect(()=>{

        console.log(gameKey)

        const authObject = {
            email: email,
            gameKey: gameKey
        };

        socket.emit("joinGame", authObject)

        socket.on('connection', (data) => {
            socket.emit("joinGame", authObject)
            console.log("hi1")
        })

        socket.on("updatePlayers", (data) => {
            console.log("hi2")
        })

        socket.on("disconnection", (data) => {
            setGameKey("");
            console.log("hi3")
        })

        socket.on("joinFail", (data) => {
            console.log("hi4")
        })

        socket.on("connect_error", (err) => {
            console.log(`connect_error due to ${err.message}`);
          });          
     
     },[email, gameKey, setGameKey])    

    return (

        <div id="blitzBout">

            <main id="blitzBoutMainWrapper" style={blitzBoutMainWrapperStyle}>
                <h1><span style={blueEmphasisStyle}>BlitzBout</span>.</h1>
            </main>

        </div>

    );

};
BlitzBoutPlay.propTypes = {
    email: PropTypes.string,
    gameKey: PropTypes.string.isRequired,
    setGameKey: PropTypes.func
}

export default BlitzBoutPlay;
