import React from 'react'
import PropTypes from 'prop-types';
import AvatarPicture from "../components/AvatarPicture"

const BlitzBoutLobby = (users, gameName, gameKey) => {

    const blitzBoutMainLobbyWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#334173",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "white"

    }

    const blitzBoutPlayerListWrapperStyle = {

        display: "flex",
        padding: "2%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",
        fontSize: "2.5em",
        fontFamily: "poppins",
        fontColor: "334173"

    }

    // #e3e3ed

    const blueEmphasisStyle = {
        color: "#334173",
        fontWeight: "bold"
    }

    const userCard = (user) => {
        return (
            <div>
                <AvatarPicture avatar = {user.avatar} dimension = "50px"/>
                <h1>{user.username}</h1>
            </div>
        )
    }
    userCard.propTypes = {
        user: PropTypes.object
    }

    return (

        <div id="blitzBoutLobby">

            <main id="blitzBoutMainLobbyWrapper" style={blitzBoutMainLobbyWrapperStyle}>
                <h1><span style={blueEmphasisStyle}>BlitzBout</span>.</h1>
                <h1>{gameKey}</h1>
                <h1>{gameName}</h1>
            </main>

            <div id="blitzBoutPlayerListWrapper" style={blitzBoutPlayerListWrapperStyle}>
                {users.map(
                    (user, index) =>
                        <userCard key={index} user = {user}/>
                )}
            </div>

        </div>

    );

};
BlitzBoutLobby.propTypes = {
    users: PropTypes.array.isRequired,
    gameName: PropTypes.string,
    gameKey: PropTypes.string
}

export default BlitzBoutLobby;
