// React libs
import React from 'react';
import { BrowserRouter, Routes, Route, Outlet, useLocation } from "react-router-dom";
// Components
import NavBar from "./components/NavBar"
import FooterComponent from './components/FooterComponent';
// Pages
import Home from "./pages/Home";
import NoPage from "./pages/NoPage"
import LogIn from './pages/LogIn';
import TermsAndConditions from './pages/TermsAndConditions';
import AboutUs from './pages/AboutUs';
import Dashboard from './pages/Dashboard';
// Hooks
import useToken from './hooks/useToken';
import useAvatar from './hooks/useAvatar';
import useUsername from './hooks/useUsername';
import useEmail from './hooks/useEmail';

export default function App() {

  const { token, setToken } = useToken();
  const { avatar, setAvatar } = useAvatar();
  const { username, setUsername} = useUsername();
  const { email, setEmail} = useEmail();

  const SecureAccountRoutes = () => {

    const location = useLocation()

    if(!token) {
      if (location.pathname === "/account/sign-up") {
        return (
          <h1>Yur</h1>
        );
      }
      return (
        <LogIn setToken={setToken} setAvatar={setAvatar} setUsername={setUsername} setEmail={setEmail}/>
      );
    }
    else {
      return (
        <Outlet/> 
      );
    }

  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element = {<><NavBar token = {token} avatar = {avatar} username = {username} /><FooterComponent /></>}>

          <Route index element={<Home />} />
          <Route path = "Home" element={<Home />} />
          <Route path = "Terms-and-Conditions" element = {<TermsAndConditions />}/>
          <Route path = "About-Us" element = {<AboutUs />}/>

          <Route path = "account/" element = {<SecureAccountRoutes />}>
            <Route path = "log-in" element = {<LogIn setToken={setToken} setAvatar={setAvatar} setUsername={setUsername} setEmail={setEmail}/>}/>
            <Route path = "dashboard" element = {<Dashboard/>}/>
            <Route path="*" element={<NoPage />} />
          </Route>

          <Route path="*" element={<NoPage />} />

        </Route>
      </Routes>
    </BrowserRouter>
  );
}
