import React from 'react';
import { useState } from 'react';
import PropTypes from 'prop-types';
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function CreateNewBlitzBoutGame({ token, email, setGameSettings }) {

    const [create, setCreate] = useState(false);
    const [gameName, setGameName] = useState("");
    const [questions, setQuestions] = useState([]);
    const [errMsg, setErrMsg] = useState("");
    const navigate = useNavigate();

    /*
        Handle update game settings
    */

    const handleAddGameSettings = (gameSetting) => {
        setGameSettings((prevSettings) => [
            ...prevSettings,
            gameSetting,
        ]);
    };

    const handleAddQuestion = (e) => {

        // prevent the form from refreshing the whole page
        e.preventDefault();

        setQuestions((prevQuestions) => [
            ...prevQuestions,
            {
                question: "",
                points: 0,
                answers: [],
            },
        ]);
    };

    const handleAddAnswer = (e, index) => {

        // prevent the form from refreshing the whole page
        e.preventDefault();

        let newArr = [...questions];

        newArr[index].answers.append({
            answerText: "",
            state: false
        });

        setQuestions(newArr);

    };

    const handleQuestionChange = (e, index) => {

        let newArr = [...questions];

        newArr[index].question = e;

        setQuestions(newArr);
    };

    const handleAnswerTextChange = (e, index, indexAnswer) => {
        let newArr = [...questions];

        newArr[index].answers[indexAnswer] = e;

        setQuestions(newArr);
    }

    /*
            handle create submit
    */

    const handleSubmit = (e) => {
        // prevent the form from refreshing the whole page
        e.preventDefault();
        // make a popup alert showing the "submitted" text

        // set configurations
        const configuration = {
            method: "post",
            url: "http://localhost:8080/games/BlitzBout/create-gameSetting",
            data: {
                token: token,
                email: email,
                gameName: gameName,
                questions: questions
            },
        };

        // make the API call
        axios(configuration)
            .then((result) => {

                handleAddGameSettings(result.data.gameSetting);
                navigate("/games/BlitzBout/create-BlitzBout-game");
                setCreate(true);

            })
            .catch((error) => {
                if (error.response.data.message) {
                    setErrMsg(error.response.data.message);
                }
                else {
                    setErrMsg("");
                }
            });

    }

    const logInPageStyle = {

        display: "flex",
        paddingTop: "3%",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#e3e3ed",

    }

    const logInFormStyle = {

        display: "flex",
        padding: "5%",
        margin: "3%",
        marginBottom: "0",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "white",
        boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
        borderRadius: "30px"

    }
    const logInFormElementStyle = {

        padding: "1%",
        margin: "1%",

    }
    const logInFormLabelStyle = {

        padding: "1%",
        marginTop: "6%",

    }

    const logInFormButtonStyle = {

        margin: "6%",

    }

    return (
        <div id="logInPage" style={logInPageStyle}>

            <Form onSubmit={(e) => handleSubmit(e)} style={logInFormStyle}>
                <h2 style={logInFormElementStyle}>Create a new game</h2>
                {/* email */}
                <Form.Group controlId="formBasicGameName">
                    <Form.Label style={logInFormLabelStyle}>Name of your game:</Form.Label>
                    <Form.Control
                        type="gameName"
                        name="gameName"
                        value={gameName}
                        onChange={(e) => setGameName(e.target.value)}
                        placeholder="Enter game name"
                        style={logInFormElementStyle}
                    />
                </Form.Group>

                {/* questions */}
                {questions.map(
                    (question, index) =>
                        <Form.Group controlId={"formBasicQuestion" + index} key={index}>
                            <Form.Label style={logInFormLabelStyle}>Question:</Form.Label>
                            <Form.Control
                                type="question"
                                name="question"
                                value={question.question}
                                onChange={(e) => handleQuestionChange(e.target.value, index)}
                                placeholder="Enter question"
                                style={logInFormElementStyle}
                            />
                            {/* questions */}
                            {question.answers.map(
                                (answer, indexAnswer) =>
                                    <Form.Group controlId={"formBasicAnswer" + indexAnswer} key={indexAnswer}>
                                        <Form.Label style={logInFormLabelStyle}>Answer:</Form.Label>
                                        <Form.Control
                                            type="answer"
                                            name="answer"
                                            value={answer.answerText}
                                            onChange={(e) => handleAnswerTextChange(e.target.value, index, indexAnswer)}
                                            placeholder="Enter Answer"
                                            style={logInFormElementStyle}
                                        />
                                    </Form.Group>
                            )}
                            {/* Add answer button */}
                            <Button
                                variant="primary"
                                type="addAnswer"
                                onClick={(e) => handleAddAnswer(e, index)}
                                style={logInFormButtonStyle}
                            >
                                Add Answer
                            </Button>

                        </Form.Group>
                )}

                {/* Add question button */}
                <Button
                    variant="primary"
                    type="addQuestion"
                    onClick={(e) => handleAddQuestion(e)}
                    style={logInFormButtonStyle}
                >
                    Add Question
                </Button>

                {/* submit button */}
                <Button
                    variant="primary"
                    type="submit"
                    onClick={(e) => handleSubmit(e)}
                    style={logInFormButtonStyle}
                >
                    Login
                </Button>

                {/* display success message */}
                {create ? (
                    <p className="text-success">You Are Logged in Successfully</p>
                ) : (
                    <p className="text-danger">{errMsg ? errMsg : "You are not logged in"}</p>
                )}

            </Form>

        </div>
    )
}
CreateNewBlitzBoutGame.propTypes = {
    token: PropTypes.string.isRequired,
    email: PropTypes.string.isRequired,
    setGameSettings: PropTypes.func.isRequired,
}
